mapper.select:
		select u.rz,u.openid,u.user_type,u.user_code,u.idcardno,u.register_time,u.user_id, u.dept_id, u.login_name, u.user_name, u.email, u.avatar, u.phonenumber, u.password, u.sex, u.salt, u.status, u.login_ip, u.login_date, u.create_by, u.create_time, u.remark, d.dept_name, d.leader from sys_user u
		left join sys_dept d on u.dept_id = d.dept_id
		where u.delete_flag = #{deleteFlag}

            and (
            u.user_name like concat('%',#{searchKey},'%') or
            u.user_code like concat('%',#{searchKey},'%') or
            u.idcardno like concat('%',#{searchKey},'%') or
            u.phonenumber like concat('%',#{searchKey},'%')
            )


			AND u.login_name like concat('%', #{loginName}, '%')


			AND u.user_type = #{userType}


			AND u.status = #{status}


			AND u.rz = #{rz}


			AND u.phonenumber like concat('%', #{phonenumber}, '%')


            AND u.idcardno like concat('%', #{idcardno}, '%')


            AND u.user_name like concat('%', #{userName}, '%')


			AND date_format(u.create_time,'%y%m%d') >= date_format(#{params.beginTime},'%y%m%d')


			AND date_format(u.create_time,'%y%m%d') <= date_format(#{params.endTime},'%y%m%d')


			AND (u.dept_id = #{deptId} OR u.dept_id IN ( SELECT t.dept_id FROM sys_dept t WHERE FIND_IN_SET (#{deptId},ancestors) ))


		${params.dataScope}

select count(*) from sys_user u
where u.delete_flag = #{deleteFlag}

    AND u.user_type = #{userType}


    AND u.status = #{status}

select distinct u.openid, u.user_id, u.dept_id, u.login_name, u.user_name, u.email, u.avatar, u.phonenumber, u.status, u.create_time from sys_user u left join sys_dept d on u.dept_id = d.dept_id left join sys_user_role ur on u.user_id = ur.user_id left join sys_role r on r.role_id = ur.role_id where u.delete_flag = #{deleteFlag} and r.role_id = #{roleId}
AND u.login_name like concat('%', #{loginName}, '%')
AND u.phonenumber like concat('%', #{phonenumber}, '%')
AND u.idcardno like concat('%', #{idcardno}, '%')
AND u.user_name like concat('%', #{userName}, '%')
${params.dataScope}


where 

(
  u.login_name = #{loginName} or 
  u.email = #{loginName} or
  u.phonenumber = #{loginName}

)
and u.delete_flag = #{deleteFlag}

mapper.update:
update sys_user
dept_id = #{deptId}, login_name = #{loginName}, user_name = #{userName}, email = #{email}, phonenumber = #{phonenumber}, sex = #{sex}, avatar = #{avatar}, password = #{password}, salt = #{salt}, status = #{status}, login_ip = #{loginIp}, login_date = #{loginDate}, update_by = #{updateBy}, remark = #{remark}, user_code = #{userCode}, idcardno = #{idcardno}, oldpwd = #{oldpwd}, update_time = sysdate()
where user_id = #{userId}
